import { useState } from "react";
import { Link, useNavigate } from "react-router-dom";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate(); // Hook to perform navigation

  const handleLogin = (e) => {
    e.preventDefault();
    // Handle login logic here (e.g., authentication, API calls)
    console.log("Login with:", email, password);

    // Redirect user to Registration page upon login button click
    navigate("/register"); // Redirects to the Registration page
  };

  return (
    <div className="container">
      <h2>Login</h2>
      <form onSubmit={handleLogin}>
        {/* ... */}
        <button type="submit" className="btn btn-primary">
          Login
        </button>
      </form>
      <p>
        Don't have an account? <Link to="/register">Register</Link>
      </p>
      {/* ... */}
    </div>
  );
};

export default Login;
