import React, { useState } from "react";
import { useHistory } from "react-router-dom"; // Import useHistory hook for navigation

const Login = () => {
  const [showRegister, setShowRegister] = useState(false);
  const history = useHistory();

  const handleRegisterClick = () => {
    setShowRegister(true); // Set state to display the registration form
  };

  const handleLoginClick = () => {
    // Logic for handling login (if needed)
    // Redirect to user dashboard or home page on successful login
    history.push("/dashboard"); // Replace '/dashboard' with the appropriate route
  };

  return (
    <div>
      {!showRegister ? (
        <div>
          {/* Login form */}
          <h2>Login</h2>
          {/* ...Login form fields and logic */}
          <button onClick={handleRegisterClick}>Register</button>
          <button onClick={handleLoginClick}>Login</button>
        </div>
      ) : (
        <div>
          {/* Registration form */}
          <h2>Register</h2>
          {/* ...Registration form fields and logic */}
          <button onClick={() => setShowRegister(false)}>Back to Login</button>
          <button>Register</button>
        </div>
      )}
    </div>
  );
};

export default Login;

